import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { search_busses } from '../apicalls/user';
import { verify_jwt } from "../apicalls/axiosInstance";
import './HomePage.css'; // Ensure you have relevant CSS for the modal and layout

const HomePage = () => {
    const navigate = useNavigate();
    const [buses, setBuses] = useState([]);
    const [sourceLatitude, setSourceLatitude] = useState("");
    const [sourceLongitude, setSourceLongitude] = useState("");
    const [destinationLatitude, setDestinationLatitude] = useState("");
    const [destinationLongitude, setDestinationLongitude] = useState("");
    const [selectedSeatPlan, setSelectedSeatPlan] = useState(null); // For seat plan
    const [isModalOpen, setIsModalOpen] = useState(false); // Modal visibility

    const handleManageBookings = () => {
        navigate("/manage-bookings");
    };

    const viewSeatPlan = (seatPlan) => {
        setSelectedSeatPlan(seatPlan); // Set seat plan to state
        setIsModalOpen(true); // Open modal
    };

    const handleLogout = () => {
        localStorage.removeItem('JWT');  // Clear token on logout
        navigate("/login");
    };    const handleSeatSelect = (seatNumber) => {
      if (selectedSeats.includes(seatNumber)) {
          // If the seat is already selected, unselect it
          setSelectedSeats(selectedSeats.filter((seat) => seat !== seatNumber));
      } else {
          // Otherwise, add it to the selected seats
          setSelectedSeats([...selectedSeats, seatNumber]);
      }
  };

