import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Ensure you have this import
import { search_busses } from '../apicalls/user'; // Adjust the import as necessary
import { verify_jwt } from "../apicalls/axiosInstance";

  const handleManageBookings = () => {
    navigate("/manage-bookings");
  };
  const viewSeatPlan = (busId) => {
    navigate(`/seat-plan/${busId}`); // Assuming you have a route for seat plan details
  };
  const handleLogout = () => {
    localStorage.removeItem('JWT');  // Clear token on logout
    navigate("/login");
  };
const HomePage = () => {
    const navigate = useNavigate();
    const [buses, setBuses] = useState([]);
    const [sourceLatitude, setSourceLatitude] = useState("");
    const [sourceLongitude, setSourceLongitude] = useState("");
    const [destinationLatitude, setDestinationLatitude] = useState("");
    const [destinationLongitude, setDestinationLongitude] = useState("");

    useEffect(() => {
        const token = localStorage.getItem('JWT');

        // Only proceed if token exists
        if (token) {
            const verifyToken = async () => {
                try {
                    const response = await verify_jwt(token); // Call verify_jwt only once

                    if (response === "-1" || response === "-2") {
                        console.log("Invalid token, redirecting to login");
                        navigate("/login");
                    }
                } catch (error) {
                    console.error("Error verifying JWT:", error);
                    navigate("/login");
                }
            };

            verifyToken();
        } else {
            navigate("/login");
        }
    }, [navigate]);

    const handleSearch = async (e) => {
        e.preventDefault();

        if (
            sourceLatitude === "" ||
            sourceLongitude === "" ||
            destinationLatitude === "" ||
            destinationLongitude === ""
        ) {
            alert("Please fill in both source and destination coordinates.");
            return;
        }

        // Additional validation: Check latitude and longitude ranges
        if (
            sourceLatitude < -1 || sourceLatitude > 20 ||
            destinationLatitude < -1 || destinationLatitude > 20 ||
            sourceLongitude < -1 || sourceLongitude > 20 ||
            destinationLongitude < -1 || destinationLongitude > 20
        ) {
            alert("Invalid latitude or longitude values. Please enter valid coordinates.");
            return;
        }

        // Fetch buses here
        try {
            const busData = await search_busses(sourceLatitude, sourceLongitude, destinationLatitude, destinationLongitude);
            console.log(busData); // Check the structure of the bus data
            setBuses(busData); // Set the fetched bus data to state
        } catch (err) {
            console.error('Error fetching buses:', err);
        }
    };

    return (

      <div className="homepage-container">
        <div className="top-buttons">
          <button onClick={handleManageBookings} className="top-btn">
            Manage Bookings
          </button>
          <button onClick={handleLogout} className="top-btn">
            Logout
          </button>
        </div>

        <div className="search-container">
          <h1>Bus Search</h1>
          <form onSubmit={handleSearch} className="horizontal-form">
            <div className="coordinate-input">
              <label>Source Latitude</label>
              <input
                type="number"
                value={sourceLatitude}
                onChange={(e) => setSourceLatitude(e.target.value)}
                placeholder="Source Latitude"
                required
              />
            </div>
            <div className="coordinate-input">
              <label>Source Longitude</label>
              <input
                type="number"
                value={sourceLongitude}
                onChange={(e) => setSourceLongitude(e.target.value)}
                placeholder="Source Longitude"
                required
              />
            </div>
            <div className="coordinate-input">
              <label>Destination Latitude</label>
              <input
                type="number"
                value={destinationLatitude}
                onChange={(e) => setDestinationLatitude(e.target.value)}
                placeholder="Destination Latitude"
                required
              />
            </div>
            <div className="coordinate-input">
              <label>Destination Longitude</label>
              <input
                type="number"
                value={destinationLongitude}
                onChange={(e) => setDestinationLongitude(e.target.value)}
                placeholder="Destination Longitude"
                required
              />
            </div>
            <button type="submit" className="search-btn">
              Search Buses
            </button>
          </form>

          <div className="bus-list">
                {buses.map((bus) => (
                    <div key={bus.busId} className={`bus-item ${bus.live ? 'live' : 'not-live'}`} style={{ backgroundColor: bus.live ? 'lightgreen' : 'lightcoral' }}>
                        <h3>{bus.busName} (ID: {bus.busId})</h3>
                        <p>Total Seats: {bus.totalSeats}</p>
