import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Ensure you have this import
import { search_busses } from '../apicalls/user'; // Adjust the import as necessary
import { verify_jwt } from "../apicalls/axiosInstance";
const HomePage = () => {
    const navigate = useNavigate();
    const [buses, setBuses] = useState([]);
    const [sourceLatitude, setSourceLatitude] = useState("");
    const [sourceLongitude, setSourceLongitude] = useState("");
    const [destinationLatitude, setDestinationLatitude] = useState("");
    const [destinationLongitude, setDestinationLongitude] = useState("");

    useEffect(() => {
        const token = localStorage.getItem('JWT');

        // Only proceed if token exists
        if (token) {
            const verifyToken = async () => {
                try {
                    const response = await verify_jwt(token); // Call verify_jwt only once

                    if (response === "-1" || response === "-2") {
                        console.log("Invalid token, redirecting to login");
                        navigate("/login");
                    }
                } catch (error) {
                    console.error("Error verifying JWT:", error);
                    navigate("/login");
                }
            };

            verifyToken();
        } else {
            navigate("/login");
        }
    }, [navigate]);

    const handleSearch = async (e) => {
        e.preventDefault();

        if (
            sourceLatitude === "" ||
            sourceLongitude === "" ||
            destinationLatitude === "" ||
            destinationLongitude === ""
        ) {
            alert("Please fill in both source and destination coordinates.");
            return;
        }

        // Additional validation: Check latitude and longitude ranges
        if (
            sourceLatitude < -1 || sourceLatitude > 20 ||
            destinationLatitude < -1 || destinationLatitude > 20 ||
            sourceLongitude < -1 || sourceLongitude > 20 ||
            destinationLongitude < -1 || destinationLongitude > 20
        ) {
            alert("Invalid latitude or longitude values. Please enter valid coordinates.");
            return;
        }

        // Fetch buses here
        try {
            const busData = await search_busses(sourceLatitude, sourceLongitude, destinationLatitude, destinationLongitude);
            console.log(busData); // Check the structure of the bus data
            setBuses(busData); // Set the fetched bus data to state
        } catch (err) {
            console.error('Error fetching buses:', err);
        }
    };

    return (
        <div>
            <form onSubmit={handleSearch}>
                {/* Input fields for source and destination coordinates */}
                <input type="text" placeholder="Source Latitude" value={sourceLatitude} onChange={(e) => setSourceLatitude(e.target.value)} />
                <input type="text" placeholder="Source Longitude" value={sourceLongitude} onChange={(e) => setSourceLongitude(e.target.value)} />
                <input type="text" placeholder="Destination Latitude" value={destinationLatitude} onChange={(e) => setDestinationLatitude(e.target.value)} />
                <input type="text" placeholder="Destination Longitude" value={destinationLongitude} onChange={(e) => setDestinationLongitude(e.target.value)} />
                <button type="submit">Search Buses</button>
            </form>
            {/* Render the buses here */}
            <div>
                {buses.map(bus => (
                    <div key={bus.busId}>
                        <h2>{bus.busName}</h2>
                        <p>Total Seats: {bus.totalSeats}</p>
                        <p>Current Occupancy: {bus.currentOccupancy}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HomePage;
