export const verify_jwt = async (token) => {

    await fetch("http://localhost:5001" + '/auth/jwt', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*'
        },

        body:JSON.stringify({
            mobileNumber: mobileNumber, // ensure this is a string
            otp: otp                   // ensure this is an integer
        })
    })
        .then(response => response.text())
        .then(data => {
            console.log(data)
            if (data === "1")
            {
                return "1";
            } else if (
                data==="2"
            )
            {
                return "2";
            }
            else 
                return "0"
        })
        .catch(error => {
            console.log(error)
        });
};
