export const loginUser = async (mobileNumber,Role) => {
    try {
        const response = await fetch('http://localhost:5001/auth/send_otp', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                mobileNumber: mobileNumber, // ensure this is a string
                role: Role                   // ensure this is an integer
            })
        });

        if (response.ok) {
            const data = await response.text();
            return data === "pending" ? "pending" : data;
        } else {
            console.log(`Request failed with status: ${response.status}`);
            return "error";
        }
    } catch (error) {
        console.error("Error:", error);
        throw error;
    }
};

